import React from 'react';
import styled from 'styled-components';
import {PRODUCTS} from './productsData';

class ProductInfo extends React.Component {
  render() {
    const {params} = this.props.match;
    const id = parseInt(params.id, 10);
    return(
      <React.StrictMode>
        <TITLE>{PRODUCTS[id].title}</TITLE>
        <Wrapper>
          <IMG src={`${process.env.PUBLIC_URL}/images/${PRODUCTS[id].image}`} alt="" />
          <CONTENT>
            <p>{PRODUCTS[id].text1}</p>
            <p>{PRODUCTS[id].text2}</p>
            <p>{PRODUCTS[id].price}</p>
            <p>{PRODUCTS[id].size}</p>
            <p>{PRODUCTS[id].color}</p>
            <p>{PRODUCTS[id].material}</p>
          </CONTENT>
        </Wrapper>        
      </React.StrictMode>
    );
  }
}

const Wrapper = styled.div`
  display: flex;
`;

const TITLE = styled.h1`
  font-size: 14px;
  font-weight: normal;
  margin-bottom: 30px;
`;

const IMG = styled.img`
  width: 400px;
  height: 400px;
`;

const CONTENT = styled.div`
  margin-left: 40px;
`;

export default ProductInfo;
